import React, { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';
import * as R from 'ramda';
import { environment } from '../../environments/environment';
import NavBar from '../NavBar/NavBar';
import GoogleMap from '../GoogleMap/GoogleMap';

const Main = () => {
  const [cookies] = useCookies(['tokenCookie']);
  const [userData, setUserData] = useState({});
  const [spots, setSpots] = useState([]);
  const [favouriteSpots, setFavouriteSpots] = useState([]);

  useEffect(() => {
    axios
      .get(`${environment.apiPath}/user/${cookies.tokenCookie}`)
      .then((response) => {
        setUserData(response);
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
      });

    axios
      .get(`${environment.apiPath}/favourites`)
      .then((response) => {
        setFavouriteSpots(R.uniq(response.data.map(sport => sport.spot.toString())))
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
    });

  }, [cookies])

  useEffect(() => {
    axios
    .get(`${environment.apiPath}/spot`)
    .then((response) => {
      const spots = response.data.map(spot => (
        R.isEmpty(favouriteSpots.filter(favSpot => favSpot === spot.id))
          ? spot
          : {...spot, isFavouriteSpot: true}
      ))
      setSpots(spots);

    })
    .catch((error) => {
      if (error.response) {
        // toast("Oups ! Something went wrong");
      }
    });
  }, [favouriteSpots])


  return (
    <div className="main d-flex flex-wrap justify-content-center">
      <NavBar userInfo={userData} />
      <GoogleMap className="mt-5" spots={spots} />
    </div>
  );
};

export default Main;
