import React, { useEffect } from 'react';
import { useCookies } from 'react-cookie';

const Main = () => {
  const [cookies] = useCookies(['tokenCookie']);
  useEffect(() => {
    axios
      .get(`${environment.apiPath}/user/:${cookies}`)
      .then((response) => {
        onSignIn(response.data.userId);
        setIsLoading(false);
      })
      .catch((error) => {
        if (error.response) {
          toast("Oups ! Something went wrong");
          setIsLoading(false);
        }
      });
  }, [])

  return (
    <div className="main">

    </div>
  );
};

export default Main;
