import React, { useState } from 'react'
import GoogleMapReact from 'google-map-react'
import './GoogleMap.scss'
import LocationPin from './LocationPin';
import SpotInfoCard from './SpotInfoCard';
import * as R from 'ramda';

const Map = ({ location, zoomLevel, className, spots }) => {
  const [ openedSpot, setOpenedSpot ] = useState({});

  const onSpotClick = ({ lat, lng }) => {
    console.log(lat);
    setOpenedSpot({ lat, lng })
  }

  console.log(openedSpot);

  return (
    <div className={`google-map ${className}`}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyCDQY2Gatxh38awcCqZzSBF-BgwZzV2HVk' }}
        defaultCenter={{
          lat: 59.95,
          lng: 30.33
        }}
        defaultZoom={1}
      >
        { spots &&
          spots.map(spot => 
            <LocationPin
              lat={spot.lat}
              lng={spot.long}
              isFavouriteSpot={spot.isFavouriteSpot}
              onClick={onSpotClick}
            />  
          )
        }
        { !R.isEmpty(openedSpot) &&
          <SpotInfoCard lat={openedSpot.lat} lng={openedSpot.lng}/>
        }
      </GoogleMapReact>
    </div>
  )
}

export default Map;
