import React, { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';
import { environment } from '../../environments/environment';
import NavBar from '../NavBar/NavBar';
import GoogleMap from '../GoogleMap/GoogleMap';

const Main = () => {
  const [cookies] = useCookies(['tokenCookie']);
  const [userData, setUserData] = useState({});
  const [spots, setSpots] = useState([]);
  const [favouriteSpots, setFavouriteSpots] = useState([]);

  useEffect(() => {
    axios
      .get(`${environment.apiPath}/user/${cookies.tokenCookie}`)
      .then((response) => {
        console.log(response)
        setUserData(response);
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
      });

      axios
      .get(`${environment.apiPath}/spot`)
      .then((response) => {
        setSpots(response.data);
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
      });

      axios
      .get(`${environment.apiPath}/favourites`)
      .then((response) => {
        setFavouriteSpots(response.data);
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
      });
  }, [])

  return (
    <div className="main d-flex flex-wrap justify-content-center">
      <NavBar userInfo={userData} />
      <GoogleMap className="mt-5" spots={spots} favouriteSpots={favouriteSpots}/>
    </div>
  );
};

export default Main;
