import React, { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';
import * as R from 'ramda';
import { environment } from '../../environments/environment';
import NavBar from '../NavBar/NavBar';
import { Button, Container } from '@material-ui/core';
import GoogleMap from '../GoogleMap/GoogleMap';
import { useRefetch } from '../../providers/RefetchProvider'

const Main = () => {
  const [cookies] = useCookies(['tokenCookie']);
  const [userData, setUserData] = useState({});
  const [spots, setSpots] = useState([]);
  const [favouriteSpots, setFavouriteSpots] = useState([]);
  const [ isLoading, setIsLoading ] = useState(false);
  const { refetch } = useRefetch();

  useEffect(() => {
    axios
      .get(`${environment.apiPath}/user/${cookies.tokenCookie}`)
      .then((response) => {
        setUserData(response);
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
        }
      });

  }, [cookies])

  useEffect(() => {
    axios
      .get(`${environment.apiPath}/favourites`)
      .then((response) => {
        setIsLoading(true);
        setFavouriteSpots(
          R.uniq(response.data.map(
            spot => ({
              spot: spot.spot.toString(),
              favId: spot.id 
            })
          ))
        )
      })
      .catch((error) => {
        if (error.response) {
          // toast("Oups ! Something went wrong");
          setIsLoading(false);
        }
    });
  }, [refetch])

  useEffect(() => {
    axios
    .get(`${environment.apiPath}/spot`)
    .then((response) => {
      setTimeout(5000);
      const spots = response.data.map(spot => (
        R.isEmpty(favouriteSpots.filter(favSpot => favSpot.spot === spot.id))
          ? spot
          : { ...spot,
              isFavouriteSpot: true,
              favSpotId: favouriteSpots.filter(favSpot => favSpot.spot === spot.id)[0].favId
            }
      ))
      setIsLoading(false);
      setSpots(spots);
    })
    .catch((error) => {
      if (error.response) {
        // toast("Oups ! Something went wrong");
        setIsLoading(false);
      }
    });
  }, [favouriteSpots])

  return (
    <div className="main d-flex flex-wrap justify-content-center">
      <NavBar userInfo={userData} />
      <Container maxWidth="xl">
        <GoogleMap className="mt-5" spots={spots} isLoading={isLoading}/>
        {/* <Button
            className="bg-primary h-25"
            type="submit"
            variant="contained"
            color="primary"
          >
            ADD SPOT
        </Button> */}
      </Container>
    </div>
  );
};

export default Main;
