import React, { useState } from 'react'
import GoogleMapReact from 'google-map-react'
import './GoogleMap.scss'
import LocationPin from './LocationPin';
import SpotInfoCard from './SpotInfoCard';
import * as R from 'ramda';

const GoogleMap = ({ location, zoomLevel, className, spots, toggleFavourites }) => {
  const [ openedSpot, setOpenedSpot ] = useState({});

  const onSpotClick = (id) => {
    const spot = spots.filter(spot => spot.id === id)[0]
    const { name, country, month, probability, isFavouriteSpot } = spot
    setOpenedSpot({
      lat: Number(spot.lat),
      lng: Number(spot.long),
      name,
      country,
      month,
      probability,
      isFavouriteSpot,
      id
    })
  }

  const onToggleFavourites = (id) => {
    setOpenedSpot({});
    toggleFavourites(id);
  }

  return (
    <div className={`google-map ${className}`}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyCDQY2Gatxh38awcCqZzSBF-BgwZzV2HVk' }}
        defaultCenter={{
          lat: 59.95,
          lng: 30.33
        }}
        center = {
          !R.isEmpty(openedSpot)
            ? { lat: Number(openedSpot.lat), lng: Number(openedSpot.lng)}
            : null
        }
        defaultZoom={1}
      >
        { spots &&
          spots.map(spot => 
            <LocationPin
              lat={spot.lat}
              lng={spot.long}
              isFavouriteSpot={spot.isFavouriteSpot}
              onClick={onSpotClick}
              id={spot.id}
            />  
          )
        }
        { !R.isEmpty(openedSpot) &&
          <SpotInfoCard {...openedSpot} onToggleFavourites={onToggleFavourites}/>
        }
      </GoogleMapReact>
    </div>
  )
}

export default GoogleMap;
