import React from 'react'
import RoomIcon from '@material-ui/icons/Room';
import './GoogleMap.scss';

const LocationPin = ({ text, isFavouriteSpot, lat, lng, onClick }) => (
  <div className="pin" onClick={() => onClick(lat, lng)}>
    <RoomIcon fontSize="large" color={ isFavouriteSpot ? 'action' : 'error' }/>
    <p className="pin-text">{text}</p>
  </div>
)

export default LocationPin;
